export class Man {
    constructor(
        public name:string,
        public age:number) {
        this.name = name;
    }

    getName():string {
        return this.name;
    }

    getAge():number {
        return this.age;
    }
}

let man1 = new Man('Иван', 30);
man1.getName();
man1.getAge();
